use raven::prelude::*;

struct Product {
    id: u32,
    title: String,
    price: f64,
    image: String,
    sizes: Vec<String>,
}

component ProductCard(props: ProductCardProps) {
    let selected_size = Signal::new(String::from("M"));

    <div class="product-card">
        <img src={props.product.image.clone()} alt={props.product.title.clone()} />
        <h3>{props.product.title.clone()}</h3>
        <p class="price">${props.product.price}</p>
        <select value={selected_size.get()} onchange={|e| selected_size.set(e.target.value)}>
            {props.product.sizes.iter().map(|s| {
                <option value={s.clone()}>{s.clone()}</option>
            }).collect::<Vec<_>>()}
        </select>
        <button onclick={|| {
            let mut current_cart = props.cart.get();
            current_cart.push((props.product.id, selected_size.get()));
            props.cart.set(current_cart);
        }}>
            Add to Cart
        </button>
    </div>
}

#[derive(Props, Clone)]
struct ProductCardProps {
    product: Product,
    cart: Signal<Vec<(u32, String)>>,
}

component App() {
    let products = vec![
        Product {
            id: 1,
            title: String::from("Tropical Paradise"),
            price: 34.99,
            image: String::from("https://images.unsplash.com/photo-1602810318383-e386cc2a3ccf?w=400"),
            sizes: vec![String::from("S"), String::from("M"), String::from("L"), String::from("XL")],
        },
        Product {
            id: 2,
            title: String::from("Ocean Breeze"),
            price: 29.99,
            image: String::from("https://images.unsplash.com/photo-1583743814966-8936f5b7be1a?w=400"),
            sizes: vec![String::from("S"), String::from("M"), String::from("L"), String::from("XL")],
        },
        Product {
            id: 3,
            title: String::from("Palm Tree Party"),
            price: 39.99,
            image: String::from("https://images.unsplash.com/photo-1581655353564-df123a1eb820?w=400"),
            sizes: vec![String::from("S"), String::from("M"), String::from("L"), String::from("XL")],
        },
    ];

    let cart = Signal::new(vec![]);

    <div class="app">
        <header class="header">
            <h1>Hawaiian Shirts Store</h1>
            <div class="cart-badge">
                Cart ({cart.get().len()})
            </div>
        </header>
        <main class="product-grid">
            {products.into_iter().map(|p| {
                <ProductCard product={p} cart={cart.clone()} />
            }).collect::<Vec<_>>()}
        </main>

        <style>
            .app {
                font-family: 'Arial', sans-serif;
                background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
                min-height: 100vh;
                padding: 2rem;
            }

            .header {
                text-align: center;
                color: white;
                margin-bottom: 2rem;
                display: flex;
                justify-content: space-between;
                align-items: center;
                max-width: 1200px;
                margin: 0 auto 2rem auto;
            }

            .header h1 {
                font-size: 2.5rem;
                margin: 0;
            }

            .cart-badge {
                background: rgba(255, 255, 255, 0.2);
                padding: 0.5rem 1.5rem;
                border-radius: 25px;
                font-size: 1.1rem;
                font-weight: bold;
            }

            .product-grid {
                display: grid;
                grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
                gap: 2rem;
                max-width: 1200px;
                margin: 0 auto;
            }

            .product-card {
                background: white;
                border-radius: 15px;
                padding: 1.5rem;
                box-shadow: 0 8px 24px rgba(0, 0, 0, 0.15);
                transition: transform 0.3s ease, box-shadow 0.3s ease;
            }

            .product-card:hover {
                transform: translateY(-5px);
                box-shadow: 0 12px 32px rgba(0, 0, 0, 0.2);
            }

            .product-card img {
                width: 100%;
                height: 250px;
                object-fit: cover;
                border-radius: 10px;
                margin-bottom: 1rem;
            }

            .product-card h3 {
                font-size: 1.5rem;
                margin: 0.5rem 0;
                color: #333;
            }

            .price {
                font-size: 1.8rem;
                font-weight: bold;
                color: #764ba2;
                margin: 1rem 0;
            }

            .product-card select {
                width: 100%;
                padding: 0.75rem;
                margin: 1rem 0;
                border: 2px solid #ddd;
                border-radius: 8px;
                font-size: 1rem;
                cursor: pointer;
            }

            .product-card button {
                width: 100%;
                padding: 1rem;
                background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
                color: white;
                border: none;
                border-radius: 8px;
                font-size: 1.1rem;
                font-weight: bold;
                cursor: pointer;
                transition: opacity 0.3s ease;
            }

            .product-card button:hover {
                opacity: 0.9;
            }

            @media (max-width: 768px) {
                .header {
                    flex-direction: column;
                    gap: 1rem;
                }

                .product-grid {
                    grid-template-columns: 1fr;
                }
            }
        </style>
    </div>
}
